INSERT INTO section (resume_uuid, type, value) VALUES ('b3cb902d-2453-4698-afc5-eb434b461cbf', 'ACHIEVEMENT', '{"CLASSNAME":"ru.javawebinar.basejava.model.ListSection","INSTANCE":{"items":["С 2013 года: разработка проектов \"Разработка Web приложения\",\"Java Enterprise\", \"Многомодульный maven. Многопоточность. XML (JAXB/StAX). Веб сервисы (JAX-RS/SOAP). Удаленное взаимодействие (JMS/AKKA)\". Организация онлайн стажировок и ведение проектов. Более 1000 выпускников.","Реализация двухфакторной аутентификации для онлайн платформы управления проектами Wrike. Интеграция с Twilio, DuoSecurity, Google Authenticator, Jira, Zendesk.","Налаживание процесса разработки и непрерывной интеграции ERP системы River BPM. Интеграция с 1С, Bonita BPM, CMIS, LDAP. Разработка приложения управления окружением на стеке: Scala/Play/Anorm/JQuery. Разработка SSO аутентификации и авторизации различных ERP модулей, интеграция CIFS/SMB java сервера.","Реализация c нуля Rich Internet Application приложения на стеке технологий JPA, Spring, Spring-MVC, GWT, ExtGWT (GXT), Commet, HTML5, Highstock для алгоритмического трейдинга.","Создание JavaEE фреймворка для отказоустойчивого взаимодействия слабо-связанных сервисов (SOA-base архитектура, JAX-WS, JMS, AS Glassfish). Сбор статистики сервисов и информации о состоянии через систему мониторинга Nagios. Реализация онлайн клиента для администрирования и мониторинга системы по JMX (Jython/ Django).","Реализация протоколов по приему платежей всех основных платежных системы России (Cyberplat, Eport, Chronopay, Сбербанк), Белоруcсии(Erip, Osmp) и Никарагуа."]}}');
INSERT INTO section (resume_uuid, type, value) VALUES ('b3cb902d-2453-4698-afc5-eb434b461cbf', 'QUALIFICATIONS', '{"CLASSNAME":"ru.javawebinar.basejava.model.ListSection","INSTANCE":{"items":["JEE AS: GlassFish (v2.1, v3), OC4J, JBoss, Tomcat, Jetty, WebLogic, WSO2","Version control: Subversion, Git, Mercury, ClearCase, Perforce","DB: PostgreSQL(наследование, pgplsql, PL/Python), Redis (Jedis), H2, Oracle","MySQL, SQLite, MS SQL, HSQLDB","Languages: Java, Scala, Python/Jython/PL-Python, JavaScript, Groovy","XML/XSD/XSLT, SQL, C/C++, Unix shell scripts","Java Frameworks: Java 8 (Time API, Streams), Guava, Java Executor, MyBatis, Spring (MVC, Security, Data, Clouds, Boot), JPA (Hibernate, EclipseLink), Guice, GWT(SmartGWT, ExtGWT/GXT), Vaadin, Jasperreports, Apache Commons, Eclipse SWT, JUnit, Selenium (htmlelements)","Python: Django","JavaScript: jQuery, ExtJS, Bootstrap.js, underscore.js","Scala: SBT, Play2, Specs2, Anorm, Spray, Akka","Технологии: Servlet, JSP/JSTL, JAX-WS, REST, EJB, RMI, JMS, JavaMail, JAXB, StAX, SAX, DOM, XSLT, MDB, JMX, JDBC, JPA, JNDI, JAAS, SOAP, AJAX, Commet, HTML5, ESB, CMIS, BPMN2, LDAP, OAuth1, OAuth2, JWT","Инструменты: Maven + plugin development, Gradle, настройка Ngnix","администрирование Hudson/Jenkins, Ant + custom task, SoapUI, JPublisher, Flyway, Nagios, iReport, OpenCmis, Bonita, pgBouncer","Отличное знание и опыт применения концепций ООП, SOA, шаблонов проектрирования, архитектурных шаблонов, UML, функционального программирования","Родной русский, английский \"upper intermediate\""]}}');
INSERT INTO section (resume_uuid, type, value) VALUES ('b3cb902d-2453-4698-afc5-eb434b461cbf', 'EXPERIENCE', '{"CLASSNAME":"ru.javawebinar.basejava.model.OrganizationSection","INSTANCE":{"organizations":[{"homePage":{"name":"Java Online Projects","url":"http://javaops.ru/"},"positions":[{"startDate":{"year":2013,"month":11,"day":1},"endDate":{"year":3000,"month":1,"day":1},"title":"Автор проекта","description":"Создание, организация и проведение Java онлайн проектов и стажировок."}]},{"homePage":{"name":"Wrike","url":"https://www.wrike.com/"},"positions":[{"startDate":{"year":2014,"month":11,"day":1},"endDate":{"year":2016,"month":1,"day":1},"title":"Старший разработчик (backend)","description":"Проектирование и разработка онлайн платформы управления проектами Wrike (Java 8 API, Maven, Spring, MyBatis, Guava, Vaadin, PostgreSQL, Redis). Двухфакторная аутентификация, авторизация по OAuth1, OAuth2, JWT SSO"}]},{"homePage":{"name":"RIT Center","url":""},"positions":[{"startDate":{"year":2012,"month":4,"day":1},"endDate":{"year":2014,"month":11,"day":1},"title":"Java архитектор","description":"Организация процесса разработки системы ERP для разных окружений: релизная политика, версионирование, ведение CI (Jenkins), миграция базы (кастомизация Flyway), конфигурирование системы (pgBoucer, Nginx), AAA via SSO. Архитектура БД и серверной части системы. Разработка интергационных сервисов: CMIS, BPMN2, 1C (WebServices), сервисов общего назначения (почта, экспорт в pdf, doc, html). Интеграция Alfresco JLAN для online редактирование из браузера документов MS Office. Maven + plugin development, Ant, Apache Commons, Spring security, Spring MVC, Tomcat,WSO2, xcmis, OpenCmis, Bonita, Python scripting, Unix shell remote scripting via ssh tunnels, PL/Python"}]}]}}');
