INSERT INTO section (resume_uuid, type, value) VALUES ('a204e483-b8e0-41a0-be47-f34f78cbb9bf', 'QUALIFICATIONS', '{"CLASSNAME":"ru.javawebinar.basejava.model.ListSection","INSTANCE":{"items":["JEE AS: GlassFish (v2.1, v3), OC4J, JBoss, Tomcat, Jetty, WebLogic, WSO2","Version control: Subversion, Git, Mercury, ClearCase, Perforce","DB: PostgreSQL(наследование, pgplsql, PL/Python), Redis (Jedis), H2, Oracle","MySQL, SQLite, MS SQL, HSQLDB","Languages: Java, Scala, Python/Jython/PL-Python, JavaScript, Groovy","XML/XSD/XSLT, SQL, C/C++, Unix shell scripts","Java Frameworks: Java 8 (Time API, Streams), Guava, Java Executor, MyBatis, Spring (MVC, Security, Data, Clouds, Boot), JPA (Hibernate, EclipseLink), Guice, GWT(SmartGWT, ExtGWT/GXT), Vaadin, Jasperreports, Apache Commons, Eclipse SWT, JUnit, Selenium (htmlelements)","Python: Django","JavaScript: jQuery, ExtJS, Bootstrap.js, underscore.js","Scala: SBT, Play2, Specs2, Anorm, Spray, Akka","Технологии: Servlet, JSP/JSTL, JAX-WS, REST, EJB, RMI, JMS, JavaMail, JAXB, StAX, SAX, DOM, XSLT, MDB, JMX, JDBC, JPA, JNDI, JAAS, SOAP, AJAX, Commet, HTML5, ESB, CMIS, BPMN2, LDAP, OAuth1, OAuth2, JWT","Инструменты: Maven + plugin development, Gradle, настройка Ngnix","администрирование Hudson/Jenkins, Ant + custom task, SoapUI, JPublisher, Flyway, Nagios, iReport, OpenCmis, Bonita, pgBouncer","Отличное знание и опыт применения концепций ООП, SOA, шаблонов проектрирования, архитектурных шаблонов, UML, функционального программирования","Родной русский, английский \"upper intermediate\""]}}');
